import { terser } from "rollup-plugin-terser";
import resolve from 'rollup-plugin-node-resolve';

import build from './build';
import banner from './banner';

export default Object.assign(build, {
	plugins: build.plugins.concat([
		resolve({
			deps: true,
		}),
		terser({
			output: {
				preamble: banner,
			},
			compress: {
				drop_console: true,
			},
		}),
	]),
});
